import { createStore } from 'vuex'

// 实现本地存储
const setLocalCartList = (state) => {
  const { cartList } = state
  const cartListString = JSON.stringify(cartList)
  localStorage.cartList = cartListString
}
const getLocalCartList = ()=>{
  try {
    return JSON.parse(localStorage.cartList) || {}
  } catch (e) {
    return {}
  }
}
export default createStore({
  state: {
  // cartList:{
    //   shopId:{
    //     shopNme:'沃尔玛',
    //     productList:{
    //        productId:{
    //          _id: "1",
    //          name: "番茄 250g / 份",
    //          imgUrl: "http://www.dell-lee.com/imgs/vue3/tomato.png",
    //          sales: 10,
    //          price: 33.6,
    //          oldPrice: 39.6,
    //          count:2
    //        }
    //     }
    // }
    // cartList:{}
    cartList: getLocalCartList()
  },
  getters: {
  },
  mutations: {
    changeCartItemInfo(state, pyload) {
      const { shopId, productId, productInfo } = pyload
      // eslint-disable-next-line prefer-const
      let shopInfo = state.cartList[shopId] || {
        shopName: '',
        productList: {}
      }
      // if(!shopInfo) {shopInfo={}}
      let product = shopInfo.productList[productId]
      if (!product) {
        productInfo.count = 0
        product = productInfo
        // product.count = 0;
      }
      product.count = product.count + pyload.num

      if (pyload.num > 0) { product.check = true }
      // 等价于
      // (pyload.num > 0) && (product.check = true);
      if (product.count < 0) { product.count = 0 }
      // 等价于
      // (product.count < 0) && (product.count = 0);

      shopInfo.productList[productId] = product
      state.cartList[shopId] = shopInfo
      setLocalCartList(state)
    },
  },
  actions: {
  },
  modules: {
  }
})
